<script>
export default {
  // name: 'ComponentName',
  data () {
    return {
      region_active:false,
      oezLipetsk:['9','12'],
      industryPark:['5', '9'],
      oezRu:['1', '9', '10', '14', '15', '8', '2', '3'],
      technopark:['11'],
      button:'all'

    }
  },
  methods:{
    action(){
      return this.$emit('disableLoading', false)
    },
     activeMap(region) {
      this.$store.dispatch('square/actionDetailValue',false)
      this.region_active = true;
      if (region.currentTarget.classList.contains("active_map")) {
        this.right = true;
        this.$store.dispatch('square/actionFilterValue',true)
        this.$store.dispatch('square/actionChangeListValue',false)
        this.$store.dispatch('square/actionDetailValue',false)
        this.removeActiveClass()
        this.region_active = false;
      } 
      else if (this.region_active) {
        this.$store.dispatch('square/actionListValue',
        {value:true, name:region.path[0].id})
        this.$store.dispatch('square/actionChangeListValue',true)
        this.removeActiveClass()
        this.addClass(region.currentTarget)
        this.$store.dispatch('square/actionFilterValue',false)
      } 
      else {
         this.removeActiveClass()
      }
    },
     removeActiveClass(){
          for (let i = 0; i < svg.children.length; i++) {
          svg.children[i].classList.remove("active_map");
        }
    },
    addClass(region){
      region.classList.add("active_map");
    },
    removeActiveZone(){
       for (let i = 0; i < svg.children.length; i++) {
          svg.children[i].classList.remove("active");
        }
    },
       activeZone(zone){
         this.removeActiveZone()
            for (let i in zone){
           document.getElementById(zone[i]).classList.add('active')
         }
         
        
    }
  },
  computed:{
 
  }
}
</script>
